var Sensitivity = 100;
var SoundEnabled = true;
chrome.runtime.onMessage.addListener((message, sender) => {
    if (message == "ShowDebug") {
        document.getElementById("difcanvas").hidden = false;
        document.getElementById("maincanvas").hidden = false;
        localStorage.setItem("DebugButtonState", "enabled");
        return true;
    }
    if (message == "HideDebug") {
        document.getElementById("difcanvas").hidden = true;
        document.getElementById("maincanvas").hidden = true;
        localStorage.setItem("DebugButtonState", "disabled");
        return true;
    }
    if (message == "EnableSound") {
        SoundEnabled = true;
        localStorage.setItem("SoundButtonState", "enabled");
        return true;
    }
    if (message == "DisableSound") {
        SoundEnabled = false;
        localStorage.setItem("SoundButtonState", "disabled");
        return true;
    }
    var args = message.split(" ");
    if (args[0] == "SensChange") {
        Sensitivity = args[1];
        localStorage.setItem("Sensitivity", args[1]);
        console.log("Sens changed to: " + args[1])
    }
    return true;
});

function waitForElm(selector) {
    return new Promise(resolve => {
        if (document.querySelector(selector)) {
            return resolve(document.querySelector(selector));
        }

        const observer = new MutationObserver(mutations => {
            if (document.querySelector(selector)) {
                observer.disconnect();
                resolve(document.querySelector(selector));
            }
        });

        // If you get "parameter 1 is not of type 'Node'" error, see https://stackoverflow.com/a/77855838/492336
        observer.observe(document.body, {
            childList: true,
            subtree: true
        });
    });
}

function beep() {
    if (SoundEnabled) {
        var snd = new Audio("data:audio/wav;base64,");
        snd.play();
    }
}

waitForElm('#meow-camera video').then((elm) => {
    if (localStorage.getItem("Sensitivity") != null) {
        Sensitivity = localStorage.getItem("Sensitivity");
    }
    if (localStorage.getItem("SoundButtonState") == "disabled") {
        SoundEnabled = false;
    }

    var video = document.getElementById("meow-camera").querySelector("video");

    var scalefactor = 10;

    var difcanvas = document.createElement('canvas');
    difcanvas.id = "difcanvas";
    difcanvas.width = 1280 / scalefactor;
    difcanvas.height = 720 / scalefactor;
    difcanvas.hidden = true;
    var body = document.getElementsByTagName("body")[0];
    body.appendChild(difcanvas);

    var canvas = document.createElement('canvas');
    canvas.id = "maincanvas";
    canvas.width = 1280 / scalefactor;
    canvas.height = 720 / scalefactor;
    canvas.hidden = true;
    var body = document.getElementsByTagName("body")[0];
    body.appendChild(canvas);

    if (localStorage.getItem("DebugButtonState") == "enabled") {
        document.getElementById("difcanvas").hidden = false;
        document.getElementById("maincanvas").hidden = false;
    }

    var difctx = difcanvas.getContext('2d');;
    var ctx = canvas.getContext('2d');

    var myVar = setInterval(intervalTimer, 1000);
    var old = [];

    // sample the colour of every 50 pixels
    var sample_size = 50;
    threshold = 60;

    function intervalTimer() {
        try {
            difctx.clearRect(0, 0, difcanvas.width, difcanvas.height);
            var motion = motionDetection();
            console.log(motion.length);
            if (motion.length > Sensitivity) {
                console.log("KITTY!!!!");
                beep();
            }
        } catch (error) {
            console.error(error);
        }

    }

    function motionDetection() {
        var motion = [];
        // draw the video and get its pixels
        ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
        var data = ctx.getImageData(0, 0, canvas.width, canvas.height).data;
        // we can now loop over all the pixels of the video
        for (var y = 0; y < canvas.height; y++) {
            for (var x = 0; x < canvas.width; x++) {
                var pos = (x + y * canvas.width) * 4;
                var r = data[pos];
                var g = data[pos + 1];
                var b = data[pos + 2];
                if (old[pos] && Math.abs(old[pos].red - r) > threshold) {
                    difctx.fillStyle = 'rgb(' + [r, g, b].join(',') + ')';
                    difctx.fillRect(x * scalefactor, y * scalefactor, scalefactor, scalefactor);
                    // push the x, y and rgb values into the motion array
                    // but multiply the x and y values back up by scalefactor
                    // to get their actual screen position
                    motion.push({
                        x: x * scalefactor,
                        y: y * scalefactor,
                        r: r,
                        g: g,
                        b: b
                    });
                }
                old[pos] = {
                    red: r,
                    green: g,
                    blue: b
                };
            }
        }

        return motion;
    }
});